import { View, Text, TouchableOpacity, Image } from 'react-native'
import React from 'react';
import { StarIcon } from 'react-native-heroicons/solid';
import { LocationMarkerIcon } from 'react-native-heroicons/solid';
import { urlFor } from '../sanity';
import { useNavigation } from '@react-navigation/native';

const RestaurantCard = ({
    id,
    imgUrl,
    title,
    rating,
    genre,
    address,
    short_description,
    dishes,
    long,
    lat,
}) => {

    const navigation = useNavigation();

    return (
        <TouchableOpacity
            onPress={() => {
                navigation.navigate("Restaurant", {
                    id,
                    imgUrl,
                    title,
                    rating,
                    genre,
                    address,
                    short_description,
                    dishes,
                    long,
                    lat,
                })
            }}
            className='bg-white mr-3 shadow-xl rounded-md my-3 mx-2'>
            <Image

                style={{ resizeMode: 'contain' }}
                source={{
                    uri: urlFor(imgUrl).url(),
                }}

                className='h-36 w-64 rounded-sm mt-2'

            />
            <View className='px-1 pb-4'>
                <Text className='font-bold text-lg pt-2'>{title}</Text>
                <View className='flex-row items-center space-x-1'>
                    <StarIcon color='orange' opacity={0.5} size={22} />
                    <Text className='text-xs text-gray-500'>
                        <Text className='text-orange-300'>{rating}</Text> . {genre}
                    </Text>
                </View>

                <View className='flex-row items-center space-x-1'>
                    <LocationMarkerIcon color='gray' opacity={1} size={22} />
                    <Text className='text-xs text-gray-500'>Nearby . {address}</Text>
                </View>


            </View>
        </TouchableOpacity>
    )
}

export default RestaurantCard
