import { View, Text, ScrollView, Image, TouchableOpacity } from 'react-native'
import React, { useLayoutEffect } from 'react'
import { useNavigation, useRoute } from '@react-navigation/native';
import { urlFor } from '../sanity';
import {
    ArrowLeftIcon,
    ChevronRightIcon,
    LocationMarkerIcon,
    StarIcon,
} from "react-native-heroicons/solid";
import { QuestionMarkCircleIcon } from "react-native-heroicons/outline";

const RestaurantScreen = () => {

    const navigation = useNavigation();

    const { params: {
        id,
        imgUrl,
        title,
        rating,
        genre,
        address,
        short_description,
        dishes,
        long,
        lat,
    }
    } = useRoute();

    useLayoutEffect(() => {
        navigation.setOptions({
            headerShown: false,
        })
    }, [])
    console.log(imgUrl)

    return (
        <ScrollView>
            <View className='relative'>
                <Image
                    source={{
                        uri: urlFor(imgUrl).url()
                    }}
                    className="w-full h-56 bg-gray-300 p-4"
                />
                <TouchableOpacity onPress={navigation.goBack} className="absolute top-12 left-3 p-2 bg-gray-50 rounded-full opacity-80">
                    <ArrowLeftIcon size={20} color="orange" />
                </TouchableOpacity>
            </View>
            <View className="bg-white">
                <View className='px-4 pt-4'>
                    <Text className="text-3xl font-bold">{title}</Text>
                    <View className="flex-row space-x-2 my-1">
                        <View className="flex-row items-center space-x-1">
                            <StarIcon color="orange" opacity={1} size={22} />
                            <Text className="text-xs text-gray-500">
                                <Text className="text-orange-500">{rating}</Text> . {genre}
                            </Text>
                        </View>

                        <View className="flex-row items-center space-x-1">
                            <LocationMarkerIcon color="gray" opacity={0.5} size={22} />
                            <Text className="text-xs text-gray-500">Nearby . {address}</Text>
                        </View>
                    </View>
                    <Text className="text-gray-500 mt-2 pb-4">
                        {short_description}
                    </Text>
                </View>
            </View>
        </ScrollView>
    )
}

export default RestaurantScreen
